import React from "react";
import { useEffect } from "react";
import {
  Row,
  Col,
  TimePicker,
  Button,
  Radio,
  Card,
  Checkbox,
  Select
} from "antd";
import moment from "moment";
import { Link } from "@reach/router";

// const defaultForm = {
//   Condizione: "Condizione Temporale",
//   Checkbox: "Lun",
//   Festivo: "Festivo",
//   TuttiGG: "TuttiGG",
//   Time1: moment().valueOf(),
//   Time2: moment().valueOf(),
//   Time3: moment().valueOf(),
//   Time4: moment().valueOf(),
//   Stagioni: "autunno",
//   Temp: "10",
//   TempAttuale: "10",
//   TempMax: "10",
//   Meteo: "Nuvoloso"
// };

export const StepTempo = ({ dataForm, setDataForm }) => {
  const { Option } = Select;
  const option = [
    { label: "Condizione temporale", value: "Condizione temporale" },
    { label: "Sempre", value: "Sempre" }
  ];

  useEffect(() => {
    console.log("use Effect");
    var local = localStorage.getItem("Step Tempo");
    console.log(JSON.parse(local));
    setDataForm(JSON.parse(local) || dataForm);
  }, []);

  const week = [
    { label: "Lun", value: "Lun" },
    { label: "Mar", value: "Mar" },
    { label: "Mer", value: "Mer" },
    { label: "Gio", value: "Gio" },
    { label: "Ven", value: "Ven" },
    { label: "Sab", value: "Sab" },
    { label: "Dom", value: "dom" }
  ];

  const format = "HH:mm";

  /* const onChangeWeek = e => {
    setSelectedWeek(e);
  };

  const handleChange = value => {
    console.log(`selected ${value}`);
  };*/

  const onChange = () => {
    localStorage.setItem("Step Tempo", JSON.stringify(StepTempo));
    console.log(JSON.stringify(StepTempo) || dataForm);
  };

  return (
    <div>
      <div style={{ paddingLeft: 130 }}>
        <Radio.Group
          name="Condizione"
          options={option}
          value={dataForm.StepTempo.Condizione}
          onChange={event => {
            console.log(event);
            setDataForm({
              ...dataForm,
              StepTempo: {
                ...dataForm.StepTempo,
                Condizione: event.target.value
              }
            });
          }}
        ></Radio.Group>
      </div>
      <br></br>
      <div style={{ paddingLeft: 100 }}>
        {StepTempo.Condizione === "Condizione temporale" && (
          <Card title="Condizione Temporale" style={{ width: 800 }}>
            <Checkbox.Group
              value={dataForm.StepTempo.Checkbox}
              name="Checkbox"
              options={week}
              defaultValue={["Lun"]}
              onChange={value => {
                setDataForm({
                  ...dataForm,
                  StepTempo: {
                    ...dataForm.StepTempo,
                    Checkbox: value
                  }
                });
              }}
            />
            <Checkbox
              value={dataForm.StepTempo.Festivo}
              name="Festivo"
              defaultChecked={false}
              onChange={value => {
                setDataForm({
                  ...dataForm,
                  StepTempo: {
                    ...dataForm.StepTempo,
                    Checkbox: value
                  }
