                        defaultValue="sanGiovanni"
                        style={{ width: 160 }}
                        onChange={value => {
                          setDataForm({
                            ...dataForm,
                            StepUtente: {
                              ...StepUtente,
                              POS: value
                            }
                          });
                        }}
                      >
                        <Option value="sanGiovanni">
                          Piazza San Giovanni, 17
                        </Option>
                        <Option value="Altro"> Altro</Option>
                      </Select>
                    </Col>
                  </Row>
                </div>
                <br></br>
                <p></p>
                <Row>
                  <Col span={6}>Condizione Temporale</Col>
                  <Col span={6}>Tipologia POS</Col>
                </Row>
              </div>
              <br></br>
              <div>
                <Row>
                  <Col span={6}>
                    <Select
                      value={dataForm.StepUtente.CondizioneTemp}
                      name="CondizioneTemp"
                      defaultValue="ComboBox"
                      style={{ width: 160 }}
                      onChange={value => {
                        setDataForm({
                          ...dataForm,
                          StepUtente: {
                            ...StepUtente,
                            CondizioneTemp: value
                          }
                        });
                      }}
                    >
                      <Option value="ComboBox">ComboBox</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                  <Col span={6}>
                    <Select
                      value={dataForm.StepUtente.Tipo}
                      name="Tipo"
                      defaultValue="Camerino"
                      style={{ width: 160 }}
                      onChange={value => {
                        setDataForm({
                          ...dataForm,
                          StepUtente: {
                            ...StepUtente,
                            Tipo: value
                          }
                        });
                      }}
                    >
                      <Option value="Camerino">Camerino</Option>
                      <Option value="Altro">Altro</Option>
                    </Select>
                  </Col>
                </Row>
              </div>
            </Card>
          </div>
          <br></br>
          <p></p>

          <div style={{ paddingLeft: 100 }}>
            <Row>
              <Col span={6}>
                <Link to="/StepLocation">
                  <Button>Indietro</Button>
                </Link>
              </Col>
              <Col span={6}></Col>
              <Col span={6}></Col>

              <Col span={6}>
                <Link to="/StepActions">
                  <Button onClick={onChange}>Avanti</Button>
                </Link>
              </Col>
            </Row>
          </div>
        </div>
      </div>
    </div>
  );
