import React, { useState } from "react";
import { Home } from "./Home";
import { Steps, Divider } from "antd";
import { Router } from "@reach/router";
import { NuovaCampagna } from "./NuovaCampagna";
import { StepTempo } from "./StepTempo";
import { StepLocation } from "./StepLocation";
import { StepUtente } from "./StepUtente";
import { StepActions } from "./StepActions";
import moment from "moment";

function App() {
  const { Step } = Steps;
  const [current, setCurrent] = useState(0);
  const data = [
    {
      NomeCampagna: "Coap",
      Autore: "hjk",
      profilo: "hjk"
    }
  ];

  const [dataForm, setDataForm] = {
    nuovaCampagna: {
      nomeCampagna: "",
      dataInizio: moment().valueOf(),
      dataFine: moment().valueOf(),
      profiloCampagna: "Global Campain",
      note: "",
      Campagna: ""
    },

    StepTempo: {
      Condizione: "Condizione Temporale",
      Checkbox: "Lun",
      Festivo: "Festivo",
      TuttiGG: "TuttiGG",
      Time1: moment().valueOf(),
      Time2: moment().valueOf(),
      Time3: moment().valueOf(),
      Time4: moment().valueOf(),
      Stagioni: "autunno",
      Temp: "10",
      TempAttuale: "10",
      TempMax: "10",
      Meteo: "Nuvoloso"
    },

    StepLocation: {}
  };

  const onChange = current => {
    console.log("onChange:", current);
    setCurrent(current);
  };

  const hendleNextPage = () => {
    setCurrent(current + 1);
  };

  return (
    <div>
      <div style={{ paddingTop: 20, paddingLeft: 20, paddingRight: 20 }}>
        <h1 style={{ textAlign: "center" }}>Nuova Campagna</h1>
        <Divider></Divider>
        <Steps current={current} onChange={onChange}>
          <Step title="Step 1" description="Nuova Campagna." />
          <Step title="Step 2" description="Tempo." />
          <Step title="Step 3" description="Location." />
          <Step title="Step 4" description="Utente." />
          <Step title="Step 5" description="Actions." />
        </Steps>
        <Divider />
      </div>
      <Router>
        <Home path="/" dataVar={data}></Home>
        <NuovaCampagna
          path="/NuovaCampagna"
          onNextPage={hendleNextPage}
        ></NuovaCampagna>
        <StepTempo path="/StepTempo" onNextPage={hendleNextPage}></StepTempo>
        <StepLocation
          path="/StepLocation"
          onNextPage={hendleNextPage}
        ></StepLocation>
        <StepUtente path="/StepUtente" onNextPage={hendleNextPage}></StepUtente>
        <StepActions
          path="/StepActions"
          onNextPage={hendleNextPage}
        ></StepActions>
      </Router>
    </div>
  );
}

export default App;
