import React from "react";
import { useState, useEffect } from "react";
import { App } from "./App";
import {
  Row,
  Col,
  TimePicker,
  Button,
  Radio,
  Divider,
  Card,
  Checkbox,
  Select
} from "antd";
import moment from "moment";
import { Link } from "@reach/router";
// const defaultForm = {
//   Condizione: "Condizione Temporale",
//   Checkbox: "Lun",
//   Festivo: "Festivo",
//   TuttiGG: "TuttiGG",
//   Time1: moment().valueOf(),
//   Time2: moment().valueOf(),
//   Time3: moment().valueOf(),
//   Time4: moment().valueOf(),
//   Stagioni: "autunno",
//   Temp: "10",
//   TempAttuale: "10",
//   TempMax: "10",
//   Meteo: "Nuvoloso"
// };

export const StepTempo = () => {
  const { Option } = Select;
  const option = [
    { label: "Condizione temporale", value: "Condizione temporale" },
    { label: "Sempre", value: "Sempre" }
  ];

  const [StepTempo, setStepTempo] = useState(App.dataForm.StepTempo);

  useEffect(() => {
    console.log("use Effect");
    var local = localStorage.getItem("Step Tempo");
    console.log(JSON.parse(local));
    setStepTempo(JSON.parse(local) || defaultForm);
  }, []);

  console.log(StepTempo);
  const week = [
    { label: "Lun", value: "Lun" },
    { label: "Mar", value: "Mar" },
    { label: "Mer", value: "Mer" },
    { label: "Gio", value: "Gio" },
    { label: "Ven", value: "Ven" },
    { label: "Sab", value: "Sab" },
    { label: "Dom", value: "dom" }
  ];

  const format = "HH:mm";

  /* const onChangeWeek = e => {
    setSelectedWeek(e);
  };

  const handleChange = value => {
    console.log(`selected ${value}`);
  };*/

  const onChange = () => {
    localStorage.setItem("Step Tempo", JSON.stringify(StepTempo));
    console.log(JSON.stringify(StepTempo) || defaultForm);
  };

  return (
    <div>
      <div style={{ paddingLeft: 130 }}>
        <Radio.Group
          name="Condizione"
          options={option}
          value={StepTempo.Condizione}
          onChange={event => {
            console.log(event);
            setStepTempo({
              ...StepTempo,
              Condizione: event.target.value
            });
          }}
        ></Radio.Group>
      </div>
      <br></br>
      <div style={{ paddingLeft: 100 }}>
        {StepTempo.Condizione === "Condizione temporale" && (
          <Card title="Condizione Temporale" style={{ width: 800 }}>
            <Checkbox.Group
              value={StepTempo.Checkbox}
              name="Checkbox"
              options={week}
              defaultValue={["Lun"]}
              onChange={value => {
                setStepTempo({
                  ...StepTempo,
                  Checkbox: value
                });
              }}
            />
            <Checkbox
              value={StepTempo.Festivo}
              name="Festivo"
              defaultChecked={false}
              onChange={value => {
                setStepTempo({
                  ...StepTempo,
                  Checkbox: value
                });
              }}
            >
              Festivo
            </Checkbox>
            <div>
              <Checkbox
                value={StepTempo.TuttiGG}
                name="TuttiGG"
                style={{ paddingLeft: 444 }}
                defaultChecked={false}
                onChange={value => {
                  setStepTempo({
                    ...StepTempo,
                    Checkbox: value
                  });
                }}
              >
                Tutti i GG
              </Checkbox>
            </div>

            <div style={{ paddingLeft: 100 }}>
              <Row gutter={16}>
                <Col span={6}>
                  <p>Ora Inizio</p>
                  <TimePicker
                    value={moment(StepTempo.Time1)}
                    name="Time1"
                    format={format}
                    onChange={(time, timeSring) =>
